import React, { Component } from 'react';
import axios from 'axios'

class Login extends Component {
    constructor(props) {
        super(props);
        this.state = {
            username: '',
            password: ''
        }
    }

    async loginUser() {
        const url = MuhalikConfig.PATH + '/api/categories/field';
        data = { label: fieldValue, value: fieldValue }
        await axios.post(url, {
            data
        }, {
            headers: { 'authorization': await getUncodededTokenFromStorage() }
        }).then(function (response) {
            alert('User Login Successfully')
        }).catch(function (error) {
            alert('Error: ');
        });
    }

    render() {
        return (
            <>
                <div className='div'>

                    <label >User name:</label>
                    <input type="text" onChange={(e) => this.setState({ username: e.target.value })} placeholder="Enter user name" />

                    <br />

                    <label >Password:</label>
                    <input type="password" onChange={(e) => this.setState({ password: e.target.value })} placeholder="Enter password" />
                    <br />
                    <button onClick={this.loginUser}>Login</button>
                </div>
                <style jsx>
                    {`
                        .div {
                            margin: 10%;
                            padding: 2%;
                            background: lightgray;
                            display: flex;
                            align-items: center;
                            justify-content: center
                        }
                    `}
                </style>
            </>
        );
    }
}

export default Login
